DROP TABLE IF EXISTS `kd_about`;
CREATE TABLE IF NOT EXISTS `kd_about` (
  `id` int(5) NOT NULL AUTO_INCREMENT COMMENT '主鍵',
  `aid` int(5) NOT NULL COMMENT '序號',
