--
-- 資料表結構 `kd_markers`
--

DROP TABLE IF EXISTS `kd_markers`;
CREATE TABLE IF NOT EXISTS `kd_markers` (
  `id` int(4) NOT NULL AUTO_INCREMENT COMMENT '主鍵',
  `mid` int(4) NOT NULL COMMENT '序號',
  `name` varchar(20) COLLATE utf8_unicode_ci NOT NULL COMMENT '名稱',
  `address` varchar(20) COLLATE utf8_unicode_ci NOT NULL COMMENT '地址',
  `kana` text COLLATE utf8_unicode_ci COMMENT '假名',
  `hanasi` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '出處',
  `yoru` int(2) NOT NULL COMMENT '夜數',
  `img` text COLLATE utf8_unicode_ci COMMENT '圖檔',
  `lat` float(10,6) NOT NULL COMMENT '緯度',
  `lng` float(10,6) NOT NULL COMMENT '經度',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=23 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

