--
-- 資料表結構 `kd_role`
--

DROP TABLE IF EXISTS `kd_role`;
CREATE TABLE IF NOT EXISTS `kd_role` (
  `id` int(3) NOT NULL AUTO_INCREMENT COMMENT '默認序號',
  `rid` int(3) NOT NULL COMMENT '角色序號',
  `type` varchar(20) COLLATE utf8_unicode_ci NOT NULL COMMENT '角色種類',
  `uri` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '角色URI',
  `create_time` datetime NOT NULL COMMENT '創建時間',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- 傾印資料表的資料 `kd_role`
